import { useQuery } from "@tanstack/react-query";
import { useRoute, Link } from "wouter";
import { useEffect } from "react";
import Navbar from "@/components/Navbar";
import Footer from "@/components/Footer";
import { Button } from "@/components/ui/button";
import { Phone, Mail, ArrowLeft } from "lucide-react";
import type { Product, Vendor } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";

export default function ProductDetail() {
  const [, params] = useRoute("/products/:id");
  const productId = params?.id;

  const { data: product, isLoading: productLoading } = useQuery<Product>({
    queryKey: ["/api/products", productId],
    enabled: !!productId,
  });

  useEffect(() => {
    if (productId) {
      apiRequest("POST", "/api/analytics/product-view", { productId }).catch(console.error);
    }
  }, [productId]);

  const { data: vendor, isLoading: vendorLoading } = useQuery<Vendor>({
    queryKey: ["/api/vendors", product?.vendorId],
    enabled: !!product?.vendorId,
  });

  if (productLoading || vendorLoading) {
    return (
      <div className="min-h-screen flex flex-col">
        <Navbar />
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <div className="w-16 h-16 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
            <p className="text-muted-foreground">Loading product...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen flex flex-col">
        <Navbar />
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <h2 className="text-2xl font-bold mb-2">Product not found</h2>
            <p className="text-muted-foreground">The product you're looking for doesn't exist</p>
          </div>
        </div>
      </div>
    );
  }

  const trackContact = async (contactType: string) => {
    if (!vendor?.id) return;
    try {
      await apiRequest("POST", "/api/analytics/contact-click", {
        vendorId: vendor.id,
        contactType,
      });
    } catch (error) {
      console.error("Error tracking contact:", error);
    }
  };

  const handleCall = () => {
    if (vendor?.phone) {
      trackContact("phone");
      window.location.href = `tel:${vendor.phone}`;
    }
  };

  const handleWhatsApp = () => {
    if (vendor?.whatsapp) {
      trackContact("whatsapp");
      window.open(`https://wa.me/${vendor.whatsapp.replace(/\D/g, "")}`, "_blank");
    }
  };

  const handleEmail = () => {
    if (vendor?.email) {
      trackContact("email");
      window.location.href = `mailto:${vendor.email}`;
    }
  };

  const vendorInitials = vendor?.storeName
    .split(" ")
    .map((n) => n[0])
    .join("")
    .toUpperCase()
    .slice(0, 2);

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />

      <section className="py-16 bg-background flex-1">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <Link href="/">
            <Button variant="ghost" className="mb-4" data-testid="button-back">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Products
            </Button>
          </Link>

          <div className="bg-card rounded-xl shadow-lg border border-border overflow-hidden">
            <div className="grid md:grid-cols-2 gap-8 p-6 md:p-8">
              <div>
                {product.imageUrl ? (
                  <img
                    src={product.imageUrl}
                    alt={product.name}
                    className="w-full rounded-xl"
                    data-testid="img-product"
                  />
                ) : (
                  <div className="w-full aspect-square bg-muted rounded-xl flex items-center justify-center">
                    <svg
                      className="w-24 h-24 text-muted-foreground"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth="2"
                        d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z"
                      />
                    </svg>
                  </div>
                )}
              </div>

              <div>
                <div className="flex items-start justify-between mb-4">
                  <div>
                    <h2 className="text-3xl font-bold mb-2" data-testid="text-product-name">
                      {product.name}
                    </h2>
                  </div>
                  {product.status === "active" && (
                    <span className="px-3 py-1 bg-accent text-accent-foreground text-sm font-semibold rounded-full">
                      Active
                    </span>
                  )}
                </div>

                <div className="mb-6">
                  <div className="text-4xl font-bold text-primary mb-2" data-testid="text-product-price">
                    ${product.price}
                  </div>
                </div>

                <div className="mb-6">
                  <h3 className="font-semibold mb-3">Description</h3>
                  <p className="text-muted-foreground leading-relaxed" data-testid="text-product-description">
                    {product.description || "No description available"}
                  </p>
                </div>

                {vendor && (
                  <div className="border-t border-border pt-6">
                    <p className="text-sm text-muted-foreground mb-3">Sold by:</p>
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-3">
                        {vendor.logoUrl ? (
                          <img
                            src={vendor.logoUrl}
                            alt={vendor.storeName}
                            className="w-12 h-12 rounded-lg object-cover"
                            data-testid="img-vendor-logo"
                          />
                        ) : (
                          <div className="w-12 h-12 bg-primary rounded-lg flex items-center justify-center">
                            <span className="text-lg font-bold text-primary-foreground">
                              {vendorInitials}
                            </span>
                          </div>
                        )}
                        <div>
                          <h4 className="font-semibold" data-testid="text-vendor-name">{vendor.storeName}</h4>
                        </div>
                      </div>
                      <Link href={`/vendors/${vendor.id}`}>
                        <Button variant="ghost" size="sm" data-testid="button-view-store">
                          View Store
                        </Button>
                      </Link>
                    </div>

                    <div className="flex flex-wrap gap-3">
                      {vendor.phone && (
                        <Button onClick={handleCall} className="contact-btn flex-1" data-testid="button-call">
                          <Phone className="w-5 h-5 mr-2" />
                          Call Vendor
                        </Button>
                      )}
                      {vendor.whatsapp && (
                        <Button
                          onClick={handleWhatsApp}
                          className="contact-btn flex-1 bg-[#25D366] hover:bg-[#25D366]/90"
                          data-testid="button-whatsapp"
                        >
                          <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 24 24">
                            <path d="M17.472 14.382c-.297-.149-1.758-.867-2.03-.967-.273-.099-.471-.148-.67.15-.197.297-.767.966-.94 1.164-.173.199-.347.223-.644.075-.297-.15-1.255-.463-2.39-1.475-.883-.788-1.48-1.761-1.653-2.059-.173-.297-.018-.458.13-.606.134-.133.298-.347.446-.52.149-.174.198-.298.298-.497.099-.198.05-.371-.025-.52-.075-.149-.669-1.612-.916-2.207-.242-.579-.487-.5-.669-.51-.173-.008-.371-.01-.57-.01-.198 0-.52.074-.792.372-.272.297-1.04 1.016-1.04 2.479 0 1.462 1.065 2.875 1.213 3.074.149.198 2.096 3.2 5.077 4.487.709.306 1.262.489 1.694.625.712.227 1.36.195 1.871.118.571-.085 1.758-.719 2.006-1.413.248-.694.248-1.289.173-1.413-.074-.124-.272-.198-.57-.347m-5.421 7.403h-.004a9.87 9.87 0 01-5.031-1.378l-.361-.214-3.741.982.998-3.648-.235-.374a9.86 9.86 0 01-1.51-5.26c.001-5.45 4.436-9.884 9.888-9.884 2.64 0 5.122 1.03 6.988 2.898a9.825 9.825 0 012.893 6.994c-.003 5.45-4.437 9.884-9.885 9.884m8.413-18.297A11.815 11.815 0 0012.05 0C5.495 0 .16 5.335.157 11.892c0 2.096.547 4.142 1.588 5.945L.057 24l6.305-1.654a11.882 11.882 0 005.683 1.448h.005c6.554 0 11.89-5.335 11.893-11.893a11.821 11.821 0 00-3.48-8.413z" />
                          </svg>
                          WhatsApp
                        </Button>
                      )}
                      {vendor.email && (
                        <Button onClick={handleEmail} variant="secondary" className="contact-btn flex-1" data-testid="button-email">
                          <Mail className="w-5 h-5 mr-2" />
                          Email
                        </Button>
                      )}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
}

client/src/pages/VendorDashboard.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import Navbar from "@/components/Navbar";
import Footer from "@/components/Footer";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { ObjectUploader } from "@/components/ObjectUploader";
import type { UploadResult } from "@uppy/core";
import type { Vendor, Product } from "@shared/schema";
import { Plus, Edit, Trash2, TrendingUp } from "lucide-react";
import { Link } from "wouter";

export default function VendorDashboard() {
  const { toast } = useToast();
  const { user, isAuthenticated, isLoading } = useAuth();
  const [, setLocation] = useLocation();
  const [showProductForm, setShowProductForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: vendor } = useQuery<Vendor | null>({
    queryKey: ["/api/vendors/me"],
    queryFn: async () => {
      const res = await fetch("/api/vendors/me");
      if (res.status === 404) return null;
      if (!res.ok) throw new Error("Failed to fetch vendor");
      return res.json();
    },
    enabled: !!user?.id,
  });

  const { data: products = [] } = useQuery<Product[]>({
    queryKey: ["/api/products", vendor?.id],
    queryFn: async () => {
      if (!vendor?.id) return [];
      const res = await fetch(`/api/products?vendorId=${vendor.id}`);
      if (!res.ok) throw new Error("Failed to fetch products");
      return res.json();
    },
    enabled: !!vendor?.id,
  });

  const createProductMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", "/api/products", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: "Success", description: "Product created successfully" });
      setShowProductForm(false);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updateProductMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      return await apiRequest("PATCH", `/api/products/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: "Success", description: "Product updated successfully" });
      setEditingProduct(null);
      setShowProductForm(false);
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteProductMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest("DELETE", `/api/products/${id}`, undefined);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      toast({ title: "Success", description: "Product deleted successfully" });
    },
    onError: (error: Error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmitProduct = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const data = {
      name: formData.get("name"),
      description: formData.get("description"),
      price: formData.get("price"),
      categoryId: formData.get("categoryId") || null,
    };

    if (editingProduct) {
      updateProductMutation.mutate({ id: editingProduct.id, data });
    } else {
      createProductMutation.mutate(data);
    }
  };

  const handleGetUploadParameters = async () => {
    const res = await fetch("/api/objects/upload", {
      method: "POST",
      credentials: "include",
    });
    const { uploadURL } = await res.json();
    return {
      method: "PUT" as const,
      url: uploadURL,
    };
  };

  const handleUploadComplete = async (
    result: UploadResult<Record<string, unknown>, Record<string, unknown>>
  ) => {
    if (result.successful.length > 0 && editingProduct) {
      const uploadURL = result.successful[0].uploadURL;
      try {
        await apiRequest("PUT", "/api/product-image", {
          imageUrl: uploadURL,
          productId: editingProduct.id,
        });
        queryClient.invalidateQueries({ queryKey: ["/api/products"] });
        toast({ title: "Success", description: "Product image uploaded" });
      } catch (error) {
        toast({
          title: "Error",
          description: "Failed to save product image",
          variant: "destructive",
        });
      }
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  if (!vendor) {
    return (
      <div className="min-h-screen flex flex-col">
        <Navbar />
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center max-w-md">
            <h2 className="text-2xl font-bold mb-4">No Vendor Account</h2>
            <p className="text-muted-foreground mb-4">
              You need to create a vendor account to access the dashboard.
            </p>
            <Button onClick={() => setLocation("/")}>Go to Homepage</Button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col">
      <Navbar />

      <section className="py-16 bg-background flex-1">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="mb-8 flex justify-between items-start">
            <div>
              <h2 className="text-3xl font-bold mb-2">Vendor Dashboard</h2>
              <p className="text-muted-foreground">Manage your store and products</p>
            </div>
            {vendor && (
              <Link href="/vendor-analytics">
                <Button variant="outline" data-testid="button-view-analytics">
                  <TrendingUp className="w-4 h-4 mr-2" />
                  View Analytics
                </Button>
              </Link>
            )}
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-card rounded-xl shadow-md border border-border p-6">
              <div className="text-3xl font-bold mb-1" data-testid="text-product-count">{products.length}</div>
              <div className="text-sm text-muted-foreground">Total Products</div>
            </div>
            <div className="bg-card rounded-xl shadow-md border border-border p-6">
              <div className="text-3xl font-bold mb-1">
                {vendor.status === "approved" ? "âœ“" : "â³"}
              </div>
              <div className="text-sm text-muted-foreground capitalize">{vendor.status}</div>
            </div>
          </div>

          <div className="bg-card rounded-xl shadow-md border border-border p-6 mb-8">
            <div className="flex justify-between items-center mb-4">
              <h3 className="font-semibold text-lg">Quick Actions</h3>
            </div>
            <div className="flex gap-4">
              <Button onClick={() => setShowProductForm(true)} data-testid="button-add-product">
                <Plus className="w-5 h-5 mr-2" />
                Add New Product
              </Button>
            </div>
          </div>

          {showProductForm && (
            <div className="bg-card rounded-xl shadow-md border border-border p-6 mb-8">
              <h3 className="font-semibold text-lg mb-4">
                {editingProduct ? "Edit Product" : "Add New Product"}
              </h3>
              <form onSubmit={handleSubmitProduct} className="space-y-4">
                <div>
                  <Label htmlFor="name">Product Name</Label>
                  <Input
                    id="name"
                    name="name"
                    defaultValue={editingProduct?.name}
                    required
                    data-testid="input-product-name"
                  />
                </div>
                <div>
                  <Label htmlFor="description">Description</Label>
                  <Textarea
                    id="description"
                    name="description"
                    defaultValue={editingProduct?.description || ""}
                    data-testid="input-product-description"
                  />
                </div>
                <div>
                  <Label htmlFor="price">Price ($)</Label>
                  <Input
                    id="price"
                    name="price"
                    type="number"
                    step="0.01"
                    defaultValue={editingProduct?.price}
                    required
                    data-testid="input-product-price"
                  />
                </div>
                {editingProduct && (
                  <div>
                    <Label>Product Image</Label>
                    <ObjectUploader
                      maxNumberOfFiles={1}
                      maxFileSize={10485760}
                      onGetUploadParameters={handleGetUploadParameters}
                      onComplete={handleUploadComplete}
                      data-testid="uploader-product-image"
                    >
                      <span>Upload Image</span>
                    </ObjectUploader>
                  </div>
                )}
                <div className="flex gap-2">
                  <Button type="submit" data-testid="button-save-product">
                    {editingProduct ? "Update" : "Create"} Product
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      setShowProductForm(false);
                      setEditingProduct(null);
                    }}
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </div>
          )}

          <div className="bg-card rounded-xl shadow-md border border-border overflow-hidden">
            <div className="p-6 border-b border-border">
              <h3 className="font-semibold text-lg">Your Products</h3>
            </div>

            {products.length === 0 ? (
              <div className="p-12 text-center">
                <p className="text-muted-foreground">No products yet. Add your first product!</p>
              </div>
            ) : (
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-muted">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                        Product
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                        Price
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                        Status
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                        Actions
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-card divide-y divide-border">
                    {products.map((product) => (
                      <tr key={product.id} className="hover:bg-muted/50" data-testid={`row-product-${product.id}`}>
                        <td className="px-6 py-4">
                          <div className="flex items-center">
                            {product.imageUrl ? (
                              <img
                                src={product.imageUrl}
                                alt={product.name}
                                className="w-10 h-10 rounded-lg object-cover mr-3"
                              />
                            ) : (
                              <div className="w-10 h-10 bg-muted rounded-lg mr-3" />
                            )}
                            <span className="font-medium">{product.name}</span>
                          </div>
                        </td>
                        <td className="px-6 py-4">
                          <span className="font-semibold">${product.price}</span>
                        </td>
                        <td className="px-6 py-4">
                          <span
                            className={`px-2 py-1 inline-flex text-xs leading-5 font-semibold rounded-full ${
                              product.status === "active"
                                ? "bg-green-100 text-green-800"
                                : product.status === "flagged"
                                ? "bg-red-100 text-red-800"
                                : "bg-amber-100 text-amber-800"
                            }`}
                          >
                            {product.status}
                          </span>
                        </td>
                        <td className="px-6 py-4">
                          <div className="flex gap-2">
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => {
                                setEditingProduct(product);
                                setShowProductForm(true);
                              }}
                              data-testid={`button-edit-${product.id}`}
                            >
                              <Edit className="w-4 h-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => {
                                if (confirm("Are you sure you want to delete this product?")) {
                                  deleteProductMutation.mutate(product.id);
                                }
                              }}
                              data-testid={`button-delete-${product.id}`}
                            >
                              <Trash2 className="w-4 h-4" />
                            </Button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
}

