client/src/pages/ListingDetail.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import type { Listing } from "@shared/schema";
import { Link, useParams, useLocation } from "wouter";
import { ArrowLeft, Phone, Mail, MessageCircle, Home, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

export default function ListingDetail() {
  const { id } = useParams();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [vendorName, setVendorName] = useState("");
  const [password, setPassword] = useState("");

  const { data: listing, isLoading } = useQuery<Listing>({
    queryKey: [`/api/listings/${id}`],
  });

  const { data: vendorListings } = useQuery<Listing[]>({
    queryKey: ["/api/listings", { role: "vendor", search: listing?.vendorName }],
    queryFn: async ({ queryKey }) => {
      const [url, params] = queryKey as [string, { role?: string; search?: string }];
      const searchParams = new URLSearchParams();
      if (params.role) searchParams.append("role", params.role);
      if (params.search) searchParams.append("search", params.search);
      const res = await fetch(`${url}?${searchParams.toString()}`);
      if (!res.ok) throw new Error("Failed to fetch listings");
      return res.json();
    },
    enabled: !!listing,
  });

  const deleteListingMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("DELETE", `/api/listings/${id}`, {
        vendorName,
        password,
      });
      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.message || "Failed to delete listing");
      }
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/listings"] });
      toast({
        title: "Success",
        description: "Listing deleted successfully",
      });
      setDeleteDialogOpen(false);
      setLocation("/vendor/browse");
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleDelete = () => {
    if (!vendorName || !password) {
      toast({
        title: "Error",
        description: "Please enter vendor name and password",
        variant: "destructive",
      });
      return;
    }
    deleteListingMutation.mutate();
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-muted-foreground">Loading...</p>
      </div>
    );
  }

  if (!listing) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-muted-foreground">Listing not found</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="bg-primary text-primary-foreground py-6 px-4">
        <div className="max-w-4xl mx-auto flex items-center gap-4">
          <Link href="/buyer/browse">
            <Button variant="ghost" size="icon" className="text-primary-foreground hover:bg-primary/80" data-testid="button-back">
              <ArrowLeft className="h-5 w-5" />
            </Button>
          </Link>
          <div>
            <h1 className="text-3xl font-bold">{listing.itemName}</h1>
            <p className="text-primary-foreground/80">by {listing.vendorName}</p>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto p-4 space-y-6">
        <Card>
          <CardHeader>
            {listing.imageUrl && (
              <img
                src={listing.imageUrl}
                alt={listing.itemName}
                className="w-full h-96 object-cover rounded-lg mb-4"
                data-testid="img-listing-detail"
              />
            )}
            <CardTitle className="text-3xl">{listing.itemName}</CardTitle>
            <CardDescription className="text-lg">{listing.vendorName}</CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="text-4xl font-bold text-primary">
              ${listing.price}
            </div>

            {listing.cropType && (
              <div className="inline-block px-4 py-2 bg-accent/20 text-accent-foreground rounded-full">
                {listing.cropType}
              </div>
            )}

            {listing.description && (
              <div>
                <h3 className="font-semibold text-lg mb-2">Description</h3>
                <p className="text-foreground whitespace-pre-wrap">{listing.description}</p>
              </div>
            )}

            <div className="border-t pt-6">
              <h3 className="font-semibold text-lg mb-4">Contact Vendor</h3>
              <div className="flex flex-col sm:flex-row gap-3">
                <Button
                  asChild
                  size="lg"
                  className="flex-1"
                  data-testid="button-call"
                >
                  <a href={`tel:${listing.contactPhone}`}>
                    <Phone className="h-5 w-5 mr-2" />
                    Call: {listing.contactPhone}
                  </a>
                </Button>
                <Button
                  asChild
                  variant="outline"
                  size="lg"
                  className="flex-1"
                  data-testid="button-whatsapp"
                >
                  <a href={`https://wa.me/${listing.contactPhone.replace(/[^0-9]/g, '')}`} target="_blank" rel="noopener noreferrer">
                    <MessageCircle className="h-5 w-5 mr-2" />
                    WhatsApp
                  </a>
                </Button>
                <Button
                  asChild
                  variant="outline"
                  size="lg"
                  className="flex-1"
                  data-testid="button-email"
                >
                  <a href={`mailto:${listing.contactEmail}`}>
                    <Mail className="h-5 w-5 mr-2" />
                    Email
                  </a>
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {vendorListings && vendorListings.length > 1 && (
          <Card>
            <CardHeader>
              <CardTitle>More from {listing.vendorName}</CardTitle>
              <CardDescription>Other products from this vendor</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                {vendorListings
                  .filter((item) => item.id !== listing.id)
                  .map((item) => (
                    <Link key={item.id} href={`/listing/${item.id}`}>
                      <Card className="card-hover cursor-pointer" data-testid={`card-suggestion-${item.id}`}>
                        <CardHeader>
                          {item.imageUrl && (
                            <img
                              src={item.imageUrl}
                              alt={item.itemName}
                              className="w-full h-32 object-cover rounded-lg mb-2"
                              data-testid={`img-suggestion-${item.id}`}
                            />
                          )}
                          <CardTitle className="text-lg">{item.itemName}</CardTitle>
                        </CardHeader>
                        <CardContent>
                          <div className="text-xl font-bold text-primary">
                            ${item.price}
                          </div>
                        </CardContent>
                      </Card>
                    </Link>
                  ))}
              </div>
            </CardContent>
          </Card>
        )}

        <div className="flex gap-4 justify-center flex-wrap">
          <Link href="/">
            <Button variant="outline" size="lg" data-testid="button-home">
              <Home className="h-5 w-5 mr-2" />
              Go Home
            </Button>
          </Link>
          <Link href="/buyer/browse">
            <Button variant="outline" size="lg" data-testid="button-browse">
              Browse All Products
            </Button>
          </Link>

          <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
            <AlertDialogTrigger asChild>
              <Button variant="destructive" size="lg" data-testid="button-delete-listing">
                <Trash2 className="h-5 w-5 mr-2" />
                Delete Listing
              </Button>
            </AlertDialogTrigger>
            <AlertDialogContent>
              <AlertDialogHeader>
                <AlertDialogTitle>Delete Listing</AlertDialogTitle>
                <AlertDialogDescription>
                  To delete this listing, please confirm your vendor name and password.
                </AlertDialogDescription>
              </AlertDialogHeader>
              <div className="space-y-4 py-4">
                <div className="space-y-2">
                  <Label htmlFor="vendor-name">Vendor Name</Label>
                  <Input
                    id="vendor-name"
                    placeholder="Enter your vendor name"
                    value={vendorName}
                    onChange={(e) => setVendorName(e.target.value)}
                    data-testid="input-delete-vendor-name"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="password">Password</Label>
                  <Input
                    id="password"
                    type="password"
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    data-testid="input-delete-password"
                  />
                </div>
              </div>
              <AlertDialogFooter>
                <AlertDialogCancel data-testid="button-cancel-delete">Cancel</AlertDialogCancel>
                <AlertDialogAction
                  onClick={(e) => {
                    e.preventDefault();
                    handleDelete();
                  }}
                  disabled={deleteListingMutation.isPending}
                  data-testid="button-confirm-delete"
                >
                  {deleteListingMutation.isPending ? "Deleting..." : "Delete"}
                </AlertDialogAction>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialog>
        </div>
      </div>
    </div>
  );
}

client/src/pages/VendorPostListing.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertListingSchema, type InsertListing } from "@shared/schema";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Link, useLocation } from "wouter";
import { ArrowLeft, Upload, Loader2, Send } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

export default function VendorPostListing() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string>("");
  const [isUploadingImage, setIsUploadingImage] = useState(false);

  const form = useForm<InsertListing>({
    resolver: zodResolver(insertListingSchema),
    defaultValues: {
      role: "vendor",
      vendorName: "",
      itemName: "",
      description: "",
      price: "",
      cropType: "",
      contactPhone: "",
      contactEmail: "",
      imageUrl: "",
      password: "",
    },
  });

  const createListingMutation = useMutation({
    mutationFn: async (data: InsertListing) => {
      const res = await apiRequest("POST", "/api/listings", data);
      if (!res.ok) {
        const errorData = await res.json().catch(() => ({ message: "Unknown error" }));
        throw new Error(errorData.message || "Failed to create listing");
      }
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/listings"] });
      toast({
        title: "Success!",
        description: "Your listing has been posted.",
      });
      setLocation("/buyer/browse");
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to post listing. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const onSubmit = async (data: InsertListing) => {
    // If there's an image, upload it first
    if (imageFile) {
      setIsUploadingImage(true);
      try {
        // Get upload URL from backend
        const uploadResponse = await fetch("/api/upload-image", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ fileName: imageFile.name }),
        });

        if (!uploadResponse.ok) {
          throw new Error("Failed to get upload URL");
        }

        const { uploadUrl, publicUrl } = await uploadResponse.json();

        // Upload image to GCS
        const uploadResult = await fetch(uploadUrl, {
          method: "PUT",
          body: imageFile,
          headers: {
            "Content-Type": imageFile.type,
          },
        });

        if (!uploadResult.ok) {
          throw new Error("Failed to upload image");
        }

        // Set the proxied URL in form data (publicUrl is now /api/images/...)
        data.imageUrl = publicUrl;
      } catch (error) {
        console.error("Image upload error:", error);
        toast({
          title: "Warning",
          description: "Failed to upload image, but listing will be created without it.",
          variant: "destructive",
        });
      } finally {
        setIsUploadingImage(false);
      }
    }

    createListingMutation.mutate(data);
  };

  const isLoading = createListingMutation.isPending || isUploadingImage;

  return (
    <div className="min-h-screen bg-background p-4">
      <div className="max-w-2xl mx-auto space-y-6 py-8">
        <div className="flex items-center gap-4">
          <Link href="/">
            <Button variant="ghost" size="icon" data-testid="button-back">
              <ArrowLeft className="h-5 w-5" />
            </Button>
          </Link>
          <div>
            <h1 className="text-3xl font-bold text-primary">Post Your Product</h1>
            <p className="text-muted-foreground">Share your crops with buyers</p>
          </div>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Product Details</CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                <FormField
                  control={form.control}
                  name="vendorName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Your Name / Farm Name *</FormLabel>
                      <FormControl>
                        <Input placeholder="John's Farm" {...field} data-testid="input-vendor-name" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="itemName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Product Name *</FormLabel>
                      <FormControl>
                        <Input placeholder="Fresh Tomatoes" {...field} data-testid="input-item-name" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="cropType"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Crop Type *</FormLabel>
                      <Select onValueChange={field.onChange} defaultValue={field.value || undefined}>
                        <FormControl>
                          <SelectTrigger data-testid="select-crop-type">
                            <SelectValue placeholder="Select crop type (required)" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="vegetables">Vegetables</SelectItem>
                          <SelectItem value="fruits">Fruits</SelectItem>
                          <SelectItem value="grains">Grains</SelectItem>
                          <SelectItem value="livestock">Livestock</SelectItem>
                          <SelectItem value="dairy">Dairy</SelectItem>
                          <SelectItem value="other">Other</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Tell buyers about your product..."
                          {...field}
                          value={field.value || ""}
                          data-testid="textarea-description"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="price"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Price *</FormLabel>
                      <FormControl>
                        <Input placeholder="10.00" {...field} data-testid="input-price" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="contactPhone"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Phone Number *</FormLabel>
                      <FormControl>
                        <Input placeholder="+1234567890" {...field} data-testid="input-phone" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="contactEmail"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email Address *</FormLabel>
                      <FormControl>
                        <Input type="email" placeholder="john@example.com" {...field} data-testid="input-email" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Password *</FormLabel>
                      <FormControl>
                        <Input type="password" placeholder="Set a password to manage your listing" {...field} data-testid="input-password" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="space-y-2">
                  <FormLabel>Product Image (Optional)</FormLabel>
                  <div className="border-2 border-dashed border-border rounded-lg p-6 text-center">
                    <input
                      type="file"
                      accept="image/*"
                      onChange={handleImageChange}
                      className="hidden"
                      id="image-upload"
                      data-testid="input-image-upload"
                    />
                    <label htmlFor="image-upload" className="cursor-pointer">
                      {imagePreview ? (
                        <img src={imagePreview} alt="Preview" className="max-h-48 mx-auto rounded-lg" />
                      ) : (
                        <div className="space-y-2">
                          <Upload className="h-10 w-10 mx-auto text-muted-foreground" />
                          <p className="text-sm text-muted-foreground">Click to upload image</p>
                        </div>
                      )}
                    </label>
                  </div>
                </div>

                <Button
                  type="submit"
                  className="w-full"
                  size="lg"
                  disabled={isLoading}
                  data-testid="button-submit-listing"
                >
                  {isLoading ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      {isUploadingImage ? "Uploading..." : "Posting..."}
                    </>
                  ) : (
                    "Post Item"
                  )}
                </Button>
              </form>
            </Form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/VendorBrowse.tsx.content:
import { useQuery } from "@tanstack/react-query";
import type { Listing } from "@shared/schema";
import { Link } from "wouter";
import { ArrowLeft, Phone, Mail, Search, Plus } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useState } from "react";

export default function VendorBrowse() {
  const [search, setSearch] = useState("");
  const [cropType, setCropType] = useState<string>("all");

  const { data: listings, isLoading } = useQuery<Listing[]>({
    queryKey: ["/api/listings", { role: "vendor", cropType: cropType === "all" ? undefined : cropType, search: search || undefined }],
    queryFn: async ({ queryKey }) => {
      const [url, params] = queryKey as [string, { role?: string; cropType?: string; search?: string }];
      const searchParams = new URLSearchParams();
      if (params.role) searchParams.append("role", params.role);
      if (params.cropType) searchParams.append("cropType", params.cropType);
      if (params.search) searchParams.append("search", params.search);
      const res = await fetch(`${url}?${searchParams.toString()}`);
      if (!res.ok) throw new Error("Failed to fetch listings");
      return res.json();
    },
  });

  const filteredListings = listings || [];

  return (
    <div className="min-h-screen bg-background">
      <div className="bg-primary text-primary-foreground py-6 px-4">
        <div className="max-w-6xl mx-auto flex items-center gap-4">
          <Link href="/">
            <Button variant="ghost" size="icon" className="text-primary-foreground hover:bg-primary/80" data-testid="button-back">
              <ArrowLeft className="h-5 w-5" />
            </Button>
          </Link>
          <div className="flex-1">
            <h1 className="text-3xl font-bold">Browse Products</h1>
            <p className="text-primary-foreground/80">Fresh crops from local vendors</p>
          </div>
          <Link href="/vendor/post">
            <Button variant="secondary" size="lg" data-testid="button-upload-product">
              <Plus className="h-5 w-5 mr-2" />
              Upload Product
            </Button>
          </Link>
        </div>
      </div>

      <div className="max-w-6xl mx-auto p-4 space-y-6">
        <div className="flex gap-4 flex-col sm:flex-row">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Search products..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                className="pl-10"
                data-testid="input-search"
              />
            </div>
          </div>
          <Select value={cropType} onValueChange={setCropType}>
            <SelectTrigger className="sm:w-48" data-testid="select-filter-crop-type">
              <SelectValue placeholder="All crop types" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All crop types</SelectItem>
              <SelectItem value="vegetables">Vegetables</SelectItem>
              <SelectItem value="fruits">Fruits</SelectItem>
              <SelectItem value="grains">Grains</SelectItem>
              <SelectItem value="livestock">Livestock</SelectItem>
              <SelectItem value="dairy">Dairy</SelectItem>
              <SelectItem value="other">Other</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {isLoading ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground">Loading products...</p>
          </div>
        ) : filteredListings.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground">No products found</p>
          </div>
        ) : (
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {filteredListings.map((listing) => (
              <Link key={listing.id} href={`/listing/${listing.id}`}>
                <Card className="card-hover cursor-pointer" data-testid={`card-listing-${listing.id}`}>
                  <CardHeader>
                    {listing.imageUrl && (
                      <img
                        src={listing.imageUrl}
                        alt={listing.itemName}
                        className="w-full h-48 object-cover rounded-lg mb-4"
                        data-testid={`img-listing-${listing.id}`}
                      />
                    )}
                    <CardTitle className="text-xl">{listing.itemName}</CardTitle>
                    <CardDescription>{listing.vendorName}</CardDescription>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    {listing.cropType && (
                      <div className="inline-block px-3 py-1 bg-accent/20 text-accent-foreground rounded-full text-sm">
                        {listing.cropType}
                      </div>
                    )}
                    {listing.description && (
                      <p className="text-sm text-muted-foreground line-clamp-2">
                        {listing.description}
                      </p>
                    )}
                    <div className="text-2xl font-bold text-primary">
                      ${listing.price}
                    </div>
                  </CardContent>
                </Card>
              </Link>
            ))}
          </div>
        )}

        <div className="text-center pt-8">
          <Link href="/crop-info">
            <Button variant="outline" data-testid="button-view-crop-info">
              Learn About Crops
            </Button>
          </Link>
        </div>
      </div>
    </div>
  );
}

server/db-init.ts.content:
import { db } from "./db";
import { sql } from "drizzle-orm";

export async function initializeDatabase() {
  try {
    console.log("Initializing database tables...");

    // Create listings table
    await db.execute(sql`
      CREATE TABLE IF NOT EXISTS listings (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        role VARCHAR NOT NULL,
        vendor_name TEXT NOT NULL,
        item_name TEXT NOT NULL,
        description TEXT,
        price DECIMAL(10, 2) NOT NULL,
        crop_type TEXT,
        contact_phone TEXT NOT NULL,
        contact_email TEXT NOT NULL,
        image_url TEXT,
        password TEXT NOT NULL,
        created_at TIMESTAMP DEFAULT NOW(),
        updated_at TIMESTAMP DEFAULT NOW()
      )
    `);
    console.log("âœ“ Created listings table");

    // Create crop_info table
    await db.execute(sql`
      CREATE TABLE IF NOT EXISTS crop_info (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        title TEXT NOT NULL,
        body TEXT NOT NULL,
        media_url TEXT,
        tags TEXT[],
        created_at TIMESTAMP DEFAULT NOW()
      )
    `);
    console.log("âœ“ Created crop_info table");

    // Create questions table
    await db.execute(sql`
      CREATE TABLE IF NOT EXISTS questions (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        title TEXT NOT NULL,
        body TEXT NOT NULL,
        author_name TEXT NOT NULL,
        created_at TIMESTAMP DEFAULT NOW()
      )
    `);
    console.log("âœ“ Created questions table");

    // Create answers table
    await db.execute(sql`
      CREATE TABLE IF NOT EXISTS answers (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        question_id VARCHAR NOT NULL,
        body TEXT NOT NULL,
        author_name TEXT NOT NULL,
        created_at TIMESTAMP DEFAULT NOW()
      )
    `);
    console.log("âœ“ Created answers table");

    console.log("âœ“ Database initialization complete");
  } catch (error) {
    console.error("Error initializing database:", error);
    throw error;
  }
}

client/src/pages/QuestionsPage.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertQuestionSchema, insertAnswerSchema, type Question, type Answer, type InsertQuestion, type InsertAnswer } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";
import { ArrowLeft, MessageCircle, Send, Loader2, Search } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { formatDistanceToNow } from "date-fns";

export default function QuestionsPage() {
  const { toast } = useToast();
  const [selectedQuestion, setSelectedQuestion] = useState<Question | null>(null);
  const [isQuestionDialogOpen, setIsQuestionDialogOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  const questionForm = useForm<InsertQuestion>({
    resolver: zodResolver(insertQuestionSchema),
    defaultValues: {
      title: "",
      body: "",
      authorName: "",
    },
  });

  const answerForm = useForm<InsertAnswer>({
    resolver: zodResolver(insertAnswerSchema),
    defaultValues: {
      questionId: "",
      body: "",
      authorName: "",
    },
  });

  const { data: questions = [], isLoading: questionsLoading } = useQuery<Question[]>({
    queryKey: ["/api/questions", searchQuery],
    queryFn: async () => {
      const url = searchQuery 
        ? `/api/questions?search=${encodeURIComponent(searchQuery)}`
        : "/api/questions";
      const res = await fetch(url);
      if (!res.ok) throw new Error("Failed to fetch questions");
      return res.json();
    },
  });

  const { data: answers = [] } = useQuery<Answer[]>({
    queryKey: ["/api/questions", selectedQuestion?.id, "answers"],
    queryFn: async () => {
      if (!selectedQuestion) return [];
      const res = await fetch(`/api/questions/${selectedQuestion.id}/answers`);
      if (!res.ok) throw new Error("Failed to fetch answers");
      return res.json();
    },
    enabled: !!selectedQuestion,
  });

  const createQuestionMutation = useMutation({
    mutationFn: async (data: InsertQuestion) => {
      const res = await apiRequest("POST", "/api/questions", data);
      if (!res.ok) {
        const errorData = await res.json().catch(() => ({ message: "Unknown error" }));
        throw new Error(errorData.message || "Failed to create question");
      }
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/questions"] });
      toast({
        title: "Success!",
        description: "Your question has been posted.",
      });
      questionForm.reset();
      setIsQuestionDialogOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to post question. Please try again.",
        variant: "destructive",
      });
    },
  });

  const createAnswerMutation = useMutation({
    mutationFn: async (data: InsertAnswer) => {
      const res = await apiRequest("POST", "/api/answers", data);
      if (!res.ok) {
        const errorData = await res.json().catch(() => ({ message: "Unknown error" }));
        throw new Error(errorData.message || "Failed to create answer");
      }
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/questions", selectedQuestion?.id, "answers"] });
      toast({
        title: "Success!",
        description: "Your answer has been posted.",
      });
      answerForm.reset();
      answerForm.setValue("questionId", selectedQuestion?.id || "");
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to post answer. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onQuestionSubmit = async (data: InsertQuestion) => {
    createQuestionMutation.mutate(data);
  };

  const onAnswerSubmit = async (data: InsertAnswer) => {
    if (!selectedQuestion) return;
    createAnswerMutation.mutate({
      ...data,
      questionId: selectedQuestion.id,
    });
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="bg-primary text-primary-foreground py-6 px-4">
        <div className="max-w-6xl mx-auto flex items-center gap-4">
          <Link href="/">
            <Button
              variant="ghost"
              size="icon"
              className="text-primary-foreground hover:bg-primary/80"
              data-testid="button-back"
            >
              <ArrowLeft className="h-5 w-5" />
            </Button>
          </Link>
          <div className="flex-1">
            <h1 className="text-3xl font-bold">Learn About Orient</h1>
            <p className="text-primary-foreground/80">Post questions and get answers from the community</p>
          </div>
          <Dialog open={isQuestionDialogOpen} onOpenChange={setIsQuestionDialogOpen}>
            <DialogTrigger asChild>
              <Button
                variant="secondary"
                size="lg"
                className="hidden sm:flex"
                data-testid="button-ask-question"
              >
                <MessageCircle className="mr-2 h-5 w-5" />
                Ask Question
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Ask a Question</DialogTitle>
                <DialogDescription>
                  Share your question with the community and get helpful answers.
                </DialogDescription>
              </DialogHeader>
              <Form {...questionForm}>
                <form onSubmit={questionForm.handleSubmit(onQuestionSubmit)} className="space-y-4">
                  <FormField
                    control={questionForm.control}
                    name="authorName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Your Name</FormLabel>
                        <FormControl>
                          <Input placeholder="John Doe" {...field} data-testid="input-question-author" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={questionForm.control}
                    name="title"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Question Title</FormLabel>
                        <FormControl>
                          <Input placeholder="What is..." {...field} data-testid="input-question-title" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={questionForm.control}
                    name="body"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Details</FormLabel>
                        <FormControl>
                          <Textarea
                            placeholder="Provide more details about your question..."
                            {...field}
                            data-testid="textarea-question-body"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <Button
                    type="submit"
                    className="w-full"
                    disabled={createQuestionMutation.isPending}
                    data-testid="button-submit-question"
                  >
                    {createQuestionMutation.isPending ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Posting...
                      </>
                    ) : (
                      <>
                        <Send className="mr-2 h-4 w-4" />
                        Post Question
                      </>
                    )}
                  </Button>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      <div className="max-w-6xl mx-auto p-4 space-y-6">
        <Dialog open={isQuestionDialogOpen} onOpenChange={setIsQuestionDialogOpen}>
          <DialogTrigger asChild>
            <Button
              variant="secondary"
              size="lg"
              className="w-full sm:hidden"
              data-testid="button-ask-question-mobile"
            >
              <MessageCircle className="mr-2 h-5 w-5" />
              Ask Question
            </Button>
          </DialogTrigger>
        </Dialog>

        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-muted-foreground" />
          <Input
            type="text"
            placeholder="Search questions... (e.g., vendor, contact, upload)"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-search-questions"
          />
        </div>

        {questionsLoading ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground">Loading questions...</p>
          </div>
        ) : questions.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground">No questions yet. Be the first to ask!</p>
          </div>
        ) : (
          <div className="space-y-4">
            {questions.map((question) => (
              <Card
                key={question.id}
                className="card-hover cursor-pointer"
                onClick={() => setSelectedQuestion(question)}
                data-testid={`card-question-${question.id}`}
              >
                <CardHeader>
                  <CardTitle className="text-xl">{question.title}</CardTitle>
                  <CardDescription>
                    Asked by {question.authorName} â€¢{" "}
                    {formatDistanceToNow(new Date(question.createdAt!), { addSuffix: true })}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <p className="text-muted-foreground line-clamp-2">{question.body}</p>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>

      {selectedQuestion && (
        <Dialog open={!!selectedQuestion} onOpenChange={() => setSelectedQuestion(null)}>
          <DialogContent className="max-w-3xl max-h-[80vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="text-2xl">{selectedQuestion.title}</DialogTitle>
              <DialogDescription>
                Asked by {selectedQuestion.authorName} â€¢{" "}
                {formatDistanceToNow(new Date(selectedQuestion.createdAt!), { addSuffix: true })}
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-6">
              <div className="border-b pb-4">
                <p className="text-foreground">{selectedQuestion.body}</p>
              </div>

              <div className="space-y-4">
                <h3 className="font-semibold text-lg">
                  Answers ({answers.length})
                </h3>

                {answers.length === 0 ? (
                  <p className="text-muted-foreground text-sm">
                    No answers yet. Be the first to answer!
                  </p>
                ) : (
                  <div className="space-y-4">
                    {answers.map((answer) => (
                      <Card key={answer.id} data-testid={`card-answer-${answer.id}`}>
                        <CardHeader>
                          <CardDescription>
                            {answer.authorName} â€¢{" "}
                            {formatDistanceToNow(new Date(answer.createdAt!), { addSuffix: true })}
                          </CardDescription>
                        </CardHeader>
                        <CardContent>
                          <p>{answer.body}</p>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                )}
              </div>

              <Form {...answerForm}>
                <form onSubmit={answerForm.handleSubmit(onAnswerSubmit)} className="space-y-4">
                  <FormField
                    control={answerForm.control}
                    name="authorName"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Your Name</FormLabel>
                        <FormControl>
                          <Input placeholder="John Doe" {...field} data-testid="input-answer-author" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={answerForm.control}
                    name="body"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Your Answer</FormLabel>
                        <FormControl>
                          <Textarea
                            placeholder="Share your answer..."
                            {...field}
                            data-testid="textarea-answer-body"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <Button
                    type="submit"
                    className="w-full"
                    disabled={createAnswerMutation.isPending}
                    data-testid="button-submit-answer"
                  >
                    {createAnswerMutation.isPending ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Posting...
                      </>
                    ) : (
                      <>
                        <Send className="mr-2 h-4 w-4" />
                        Post Answer
                      </>
                    )}
                  </Button>
                </form>
              </Form>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

